package openclassroom;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 77;
		if(number % 2 ==0)
		{
			System.out.println( number + " is an Even number");
		}
		else
		{
			System.out.println(number + " is an Odd Number");
		}

	}

}
